while True: quit(f"\nThank you for Playing!\nYour wins were {win} and your losses were {lose}.\nYou had {tie} tie.\nThe computer won {lose} and lost {win} times.") if 'octopus' in globals() and ord(input("Would you like to play again? (y/n): ").lower()) != 121 else 0; exec('import random; print("Rock beats scissors, scissors beat paper, and paper beats rock." if input("Do you know how to play Rock-Paper-Scissors? (y/n): ")== "n" else ""); win = tie = lose = octopus = 0;') if 'octopus' not in globals() else 0; print(f"The computer chose {(actions:= ["rock", "paper", "scissors"])[com_action:=random.randint(0,2)].capitalize()}\n" + ['It was a tie!', 'You Won!', 'You Lost!'][result:=((["rock", "paper", "scissors"].index(input("\nPlay 'Rock', 'Paper', or 'Scissors': ").lower()) - com_action) % 3)]); win, lose, tie = win + (result==1), lose + (result==2), tie + (result==0);
